mse1 &lt;- print(
  sum(
    residuals(
      lm(y~., data = mutate(
        tib, 
        x3 = x1^2,
        x4 = log(x2 + abs(min(x2)) + 1)
      )
      )
    )^2
  )
)

[1] 6.469568e-29




mse2 &lt;- tib |&gt;
  mutate(
    x3 = x1^2, 
    x4 = log(x2 + abs(min(x2)) + 1)
  ) %&gt;% # base pipe only goes to first arg
  lm(y ~ ., data = .) |&gt; # note the use of `.`
  residuals() |&gt;
  magrittr::raise_to_power(2) |&gt; # same as `^`(2)
  sum() |&gt;
  print()

[1] 6.469568e-29

